import React, {useState, useEffect} from "react";

const Timer = () =>{
    const [seconds, setSeconds] = useState(40)

    useEffect(() => {
             setInterval(() => {
                setSeconds(seconds => seconds -1);
            }, 1000)
    })

    return (
        <div>
            {seconds}
        </div>
    )
}

export default Timer;
