import React from "react";
import Card from "./Card";
import {connect} from 'react-redux'

const Cards = (syncTasks) => {

    let count = 0
    if(!syncTasks.length){
        return <p className='text-center'>Data is not defined</p>
    }
    return syncTasks.map(task => <Card task={task} task_id={parseInt(count++)}/>)
}


const mapStateToProps = state => {
    console.log(state)
    return{
        syncTasks: state.dataGame.dataGame
    }
}

export default connect(mapStateToProps, null)(Cards)
