import {CREATE_DATA_GAME} from "./types";

const initialState = {
    dataGame: [],
    count: 0,
}

export const appReducer = (state = initialState, action )=> {
    switch (action.type){
        case CREATE_DATA_GAME:
            return {...state, dataGame: action.payload}
        default: return state
    }
}
