import React from "react";
import Card from "./Card";
import {connect, useSelector} from 'react-redux'

export const Cardss = () => {
    let tasks = useSelector(state => state.dataGame)
    let count = 0;
    if(!tasks.length){
        return <p className='text-center'>Data is not defined</p>
    }
    return tasks.map(task => <Card task={task} task_id={ parseInt(count++)} key={count++}/>);
}




export default Cardss
